package org.taktik.icure.services.external.rest.v1.dto;

import java.util.Date;

public class PlannedSendingDto {
	private int dayOfWeek;
	private Date time;
	private int id;
	

	public PlannedSendingDto() {
	}
	
	public int getDayOfWeek() {
		return dayOfWeek;
	}

	public void setDayOfWeek(int dayOfWeek) {
		this.dayOfWeek = dayOfWeek;
	}

	

	
	public PlannedSendingDto(int id,int dayOfWeek, Date time) {
		this.dayOfWeek = dayOfWeek;
		this.time = time;
		this.id = id;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public Date getTime() {
		return time;
	}

	public void setTime(Date time) {
		this.time = time;
	}
}